#include <stddef.h>
#include <stdint.h>

#define RotateLeft(x, b) (uint64_t)(((x) << (b)) | ((x) >> (64 - (b))))

__attribute__((always_inline)) inline static uint64_t hash(uint64_t seed,
                                                           uint64_t num) {
	uint64_t x = 0x112b0e88bd1aea8dull + seed;
	#pragma clang loop unroll(full)
	for (int i = 0; i < 30; i++) {
		x ^= num;
		x ^= RotateLeft(x, 17) + num;
		x ^= RotateLeft(x, 11);
		x = x * x * x + i;
		x ^= RotateLeft(x, x % 64) - num;
	}
	return x ^ num;
}

